package com.victor.ranch.ui.adapter;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import java.util.List;/** * Created by victor on 2016/7/5. */public class MyOrderViewPagerAdapter extends FragmentPagerAdapter {    private List<Fragment> frags;    private List<String> datas;    public List<String> getDatas() {        return datas;    }    public void setDatas(List<String> datas) {        this.datas = datas;    }    public void setFrags(List<Fragment> frags) {        this.frags = frags;    }    public List<Fragment> getFrags() {        return frags;    }    public MyOrderViewPagerAdapter(FragmentManager fm) {        super(fm);    }    @Override    public CharSequence getPageTitle(int position) {        return datas.get(position);    }    @Override    public Fragment getItem(int position) {        return frags.get(position);    }    @Override    public int getCount() {        return frags == null ? 0 :frags.size();    }}